mapper.insert:
insert into PUBLIC.CHARACTER (FIRSTNAME, LASTNAME, BOOK_ID
  )
values (#{firstname,jdbcType=VARCHAR}, #{lastname,jdbcType=VARCHAR}, #{bookId,jdbcType=INTEGER}
  )

mapper.update:
update PUBLIC.CHARACTER
set FIRSTNAME = #{firstname,jdbcType=VARCHAR},
  LASTNAME = #{lastname,jdbcType=VARCHAR},
  BOOK_ID = #{bookId,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, FIRSTNAME, LASTNAME, BOOK_ID
from PUBLIC.CHARACTER
where ID = #{id,jdbcType=INTEGER}
